import React, { useState, useEffect, createContext } from "react";
import axios from "axios";
import BuyActionWindow from "./BuyActionWindow";
import SellActionWindow from "./SellActionWindow";

const GeneralContext = createContext(null);

export const GeneralContextProvider = (props) => {
  const [isBuyWindowOpen, setIsBuyWindowOpen] = useState(false);
  const [isSellWindowOpen, setIsSellWindowOpen] = useState(false);
  const [selectedStockUID, setSelectedStockUID] = useState("");
  const [holdings, setHoldings] = useState([]);
  const [positions, setPositions] = useState([]);
  const [orders, setOrders] = useState([]);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const res = await axios.get("http://localhost:3002/me", { withCredentials: true });
        setUser(res.data);
      } catch (err) {
        console.error("User not authenticated.", err);
        setLoading(false);
      }
    };
    fetchUser();
  }, []);

  const refreshData = async () => {
    if (!user) return;
    try {
      setLoading(true);
      const [h, p, o] = await Promise.all([
        axios.get("http://localhost:3002/api/allHoldings", { withCredentials: true }),
        axios.get("http://localhost:3002/api/allPositions", { withCredentials: true }),
        axios.get("http://localhost:3002/api/orders", { withCredentials: true }),
      ]);
      setHoldings(h.data);
      setPositions(p.data);
      setOrders(o.data);
    } catch (error) {
      console.error("Error refreshing data:", error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (user) {
      refreshData();
    }
  }, [user]);
  
  // ✅ DEFINE THE removeHolding FUNCTION
  const removeHolding = async (holdingId) => {
    try {
      if (!window.confirm("Are you sure you want to remove this holding? This action cannot be undone.")) {
        return;
      }
      // Call the DELETE endpoint on your backend
      await axios.delete(`http://localhost:3002/api/holdings/${holdingId}`, { withCredentials: true });
      // Refresh all the data to update the UI
      await refreshData();
    } catch (error) {
      console.error("Error removing holding:", error);
      alert("Failed to remove the holding. Please try again.");
    }
  };

  const handleOpenBuyWindow = (uid) => { /* ... */ };
  const handleCloseBuyWindow = () => { /* ... */ };
  const handleOpenSellWindow = (uid) => { /* ... */ };
  const handleCloseSellWindow = () => { /* ... */ };

  return (
    <GeneralContext.Provider
      value={{
        openBuyWindow: handleOpenBuyWindow,
        closeBuyWindow: handleCloseBuyWindow,
        openSellWindow: handleOpenSellWindow,
        closeSellWindow: handleCloseSellWindow,
        refreshData,
        user,
        holdings,
        positions,
        orders,
        loading,
        removeHolding, // ✅ ADD THE FUNCTION TO THE CONTEXT VALUE
      }}
    >
      {props.children}
      {isBuyWindowOpen && <BuyActionWindow uid={selectedStockUID} />}
      {isSellWindowOpen && <SellActionWindow uid={selectedStockUID} />}
    </GeneralContext.Provider>
  );
};

export default GeneralContext;
