import React, { useContext } from 'react';
import GeneralContext from './GeneralContext';

const Positions = () => {
  // ✅ Get the positions AND the functions to open trade windows
  const { positions, openBuyWindow, openSellWindow } = useContext(GeneralContext);

  if (!positions) {
      return <p>Loading positions...</p>;
  }

  return (
    <>
      <h3 className="title">Positions ({positions.length})</h3>
      <div className="order-table">
        <table>
          <thead>
            <tr>
              <th>Product</th>
              <th>Instrument</th>
              <th>Qty.</th>
              <th>Avg.</th>
              <th>LTP</th>
              <th>P&L</th>
              <th>Action</th> {/* ✅ Add a new column header */}
            </tr>
          </thead>
          <tbody>
            {positions.map((stock, index) => {
              // Your existing P&L logic here...
              return (
                <tr key={index}>
                  <td>{stock.product}</td>
                  <td>{stock.name}</td>
                  <td>{stock.qty}</td>
                  <td>{stock.avg.toFixed(2)}</td>
                  <td>{stock.price.toFixed(2)}</td>
                  <td>{/* Your P&L calculation */}</td>

                  {/* ✅ Add a new table cell with the conditional button */}
                  <td>
                    {stock.qty > 0 && ( // If it's a long position, show a Sell button
                      <button className="sell-action-btn" onClick={() => openSellWindow(stock.name)}>
                        Sell
                      </button>
                    )}
                    {stock.qty < 0 && ( // If it's a short position, show a Buy button
                      <button className="buy-action-btn" onClick={() => openBuyWindow(stock.name)}>
                        Buy
                      </button>
                    )}
                    {/* If qty is 0, the position is closed, so no button is shown */}
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </>
  );
};

export default Positions;
