import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";

const Menu = () => {
  const [selectedMenu, setSelectedMenu] = useState(0);
  const [isProfileDropdownOpen, setIsProfileDropdownOpen] = useState(false);
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const res = await axios.get("http://localhost:3002/me", {
          withCredentials: true,
        });

        if (res.data.success) {
          setUser(res.data.user);
        } else {
          navigate("/login"); // not logged in
        }
      } catch (err) {
        navigate("/login"); // not logged in
      }
    };
    fetchUser();
  }, [navigate]);

  const handleMenuClick = (index) => {
    setSelectedMenu(index);
  };

  const handleProfileClick = () => {
    setIsProfileDropdownOpen((prev) => !prev);
  };

  const handleLogout = async () => {
  try {
    await axios.post("http://localhost:3002/logout", {}, { withCredentials: true });
    setUser(null);

    // ðŸš€ Hard redirect to frontend (public site)
    window.location.href = "http://localhost:3000/login"; 
    // Change port/domain depending on where your frontend runs
  } catch (err) {
    console.error("Logout failed", err);
  }
};

  const menuClass = "menu";
  const activeMenuClass = "menu selected";

  return (
    <div className="menu-container">
      {/* Logo */}
      <Link to="/">
        <img src="/logo.png" style={{ width: "50px" }} alt="Logo" />
      </Link>

      {/* Menu Items */}
      <div className="menus">
        <ul>
          <li>
            <Link style={{ textDecoration: "none" }} to="/" onClick={() => handleMenuClick(0)}>
              <p className={selectedMenu === 0 ? activeMenuClass : menuClass}>Dashboard</p>
            </Link>
          </li>
          <li>
            <Link style={{ textDecoration: "none" }} to="/Orders" onClick={() => handleMenuClick(1)}>
              <p className={selectedMenu === 1 ? activeMenuClass : menuClass}>Orders</p>
            </Link>
          </li>
          <li>
            <Link style={{ textDecoration: "none" }} to="/Holdings" onClick={() => handleMenuClick(2)}>
              <p className={selectedMenu === 2 ? activeMenuClass : menuClass}>Holdings</p>
            </Link>
          </li>
          <li>
            <Link style={{ textDecoration: "none" }} to="/Positions" onClick={() => handleMenuClick(3)}>
              <p className={selectedMenu === 3 ? activeMenuClass : menuClass}>Positions</p>
            </Link>
          </li>
          <li>
            <Link style={{ textDecoration: "none" }} to="/Funds" onClick={() => handleMenuClick(4)}>
              <p className={selectedMenu === 4 ? activeMenuClass : menuClass}>Funds</p>
            </Link>
          </li>
          <li>
            <Link style={{ textDecoration: "none" }} to="/Apps" onClick={() => handleMenuClick(6)}>
              <p className={selectedMenu === 6 ? activeMenuClass : menuClass}>Apps</p>
            </Link>
          </li>
        </ul>
        <hr />

        {/* Profile */}
        <div className="profile" onClick={handleProfileClick}>
          <div
            className="avatar"
            style={{
              background: "#007bff",
              color: "#fff",
              borderRadius: "50%",
              width: "40px",
              height: "40px",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              cursor: "pointer",
            }}
          >
            {user ? user.name.slice(0, 2).toUpperCase() : "??"}
          </div>
          <p className="username">{user ? user.name : "USER"}</p>
        </div>

        {/* Dropdown */}
        {isProfileDropdownOpen && (
          <div
            className="profile-dropdown"
            style={{
              position: "absolute",
              background: "#fff",
              padding: "10px",
              border: "1px solid #ccc",
              borderRadius: "8px",
              right: "10px",
              marginTop: "5px",
            }}
          >
            <button onClick={handleLogout} className="btn btn-danger w-100">
              Logout
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Menu;
