import React, { useContext, useMemo } from 'react';
import GeneralContext from './GeneralContext'; // Make sure the path is correct
import axios from 'axios'; // ✅ Import axios

// A helper function to format numbers into the k/L/Cr style
const formatIndianCurrency = (num) => {
  if (num >= 10000000) {
    return (num / 10000000).toFixed(2) + 'Cr';
  }
  if (num >= 100000) {
    return (num / 100000).toFixed(2) + 'L';
  }
  if (num >= 1000) {
    return (num / 1000).toFixed(2) + 'k';
  }
  return num.toFixed(2);
};

const Summary = () => {
  // ✅ Get user, holdings, AND refreshData from the context
  const { user, holdings, refreshData } = useContext(GeneralContext);

  const holdingsSummary = useMemo(() => {
    // ... (your existing calculation logic - no changes needed)
  }, [holdings]);

  // ✅ This function calls the backend to settle the day's trades
  const handleSettleDay = async () => {
    if (!window.confirm("Are you sure you want to settle the day? This will move all long positions to holdings and clear daily positions.")) {
      return;
    }

    try {
      const res = await axios.post("http://localhost:3002/api/settle", {}, { withCredentials: true });
      await refreshData(); // Refresh the UI to show the changes
      alert(res.data.message); // Show a success message
    } catch (error) {
      console.error("Failed to settle day:", error);
      alert("Failed to settle day. See console for details.");
    }
  };

  if (!user || !holdings) {
    return <div>Loading summary...</div>;
  }

  const isProfit = holdingsSummary.totalPandL >= 0;

  return (
    <>
      <div className="username">
        <h6>Hi, {user.name}!</h6>
        <hr className="divider" />
      </div>

      <div className="section">
        <span><p>Equity</p></span>
        <div className="data">
          {/* ... (your equity section) ... */}
        </div>
        <hr className="divider" />
      </div>

      <div className="section">
        <span><p>Holdings ({holdings.length})</p></span>
        <div className="data">
          {/* ... (your holdings summary section) ... */}
        </div>
        <hr className="divider" />
      </div>

      {/* ✅ Add the new section for the settlement button */}
      <div className="section">
        <span><p>End of Day</p></span>
        <p style={{ fontSize: '14px', color: '#666', margin: '10px 0' }}>
          This will move all your open long positions to your holdings and clear your daily P&L.
        </p>
        <button 
          onClick={handleSettleDay} 
          style={{ 
            backgroundColor: '#387ed1', 
            color: 'white', 
            border: 'none', 
            padding: '10px 15px', 
            borderRadius: '4px', 
            cursor: 'pointer' 
          }}
        >
          Simulate End of Day Settlement
        </button>
      </div>
    </>
  );
};

export default Summary;
