import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
impo

const Menu = () => {
  const [selectedMenu, setSelectedMenu] = useState(0);
  const [isProfileDropdownOpen, setIsProfileDropdownOpen] = useState(false);
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const res = await axios.get("http://localhost:3002/me", {
          withCredentials: true,
        });
        // Correctly set the user from the response
        setUser(res.data);
      } catch (err) {
        console.error("Not authenticated:", err);
        navigate("/login");
      }
    };
    fetchUser();
  }, [navigate]);

  const handleMenuClick = (index) => {
    setSelectedMenu(index);
  };

  const handleProfileClick = () => {
    setIsProfileDropdownOpen((prev) => !prev);
  };

  const handleLogout = async () => {
    try {
      // Correctly call the /api/logout endpoint
      await axios.post(
        "http://localhost:3002/api/logout",
        {},
        { withCredentials: true }
      );
      // Redirect to the public homepage
      window.location.href = "http://localhost:3000";
    } catch (err) {
      console.error("Logout failed", err);
    }
  };

  const menuClass = "menu";
  const activeMenuClass = "menu selected";

  return (
    <div className="menu-container">
      <Link to="/">
        <img src="/logo.png" style={{ width: "50px" }} alt="Logo" />
      </Link>
      <div className="menus">
        {/* Your menu list here */}
        <ul>
          <li>
            <Link to="/Summary">Dashboard</Link>
          </li>
          <li>
            <Link to="/orders">Orders</Link>
          </li>
          <li>
            <Link to="/holdings">Holdings</Link>
          </li>
          <li>
            <Link to="/positions">Positions</Link>
          </li>
          <li>
            <Link to="/funds">Funds</Link>
          </li>
          <li>
            <Link to="/apps">Apps</Link>
          </li>
        </ul>
        <hr />
        <div className="profile" onClick={handleProfileClick}>
          <div
            className="avatar"
            style={
              {
                /* your styles */
              }
            }
          >
            {user ? user.name.slice(0, 2).toUpperCase() : "??"}
          </div>
          <p className="username">{user ? user.name : "USER"}</p>
        </div>
        {isProfileDropdownOpen && (
          <div
            className="profile-dropdown"
            style={
              {
                /* your styles */
              }
            }
          >
            <button onClick={handleLogout} className="btn btn-danger w-100">
              Logout
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Menu;
