import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";

const Menu = () => {
  const [selectedMenu, setSelectedMenu] = useState(0);
  const [isProfileDropdownOpen, setIsProfileDropdownOpen] = useState(false);
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const res = await axios.get("http://localhost:3002/me", {
          withCredentials: true,
        });

        if (res.data.success) {
          setUser(res.data.user);
        } else {
          navigate("/login"); // not logged in
        }
      } catch (err) {
        navigate("/login"); // not logged in
      }
    };
    fetchUser();
  }, [navigate]);

  const handleMenuClick = (index) => {
    setSelectedMenu(index);
  };

  const handleProfileClick = () => {
    setIsProfileDropdownOpen((prev) => !prev);
  };

  const handleLogout = async () => {
    await axios.post("http://localhost:3002/logout", {}, { withCredentials: true });
    setUser(null);
    navigate("/login");
  };

  const menuClass = "menu";
  const activeMenuClass = "menu selected";

  return (
    <div className="menu-container d-flex">
      {/* Logo */}
      <Link to="/">
        <img src="/logo.png" style={{ width: "50px" }} alt="Logo" />
      </Link>

      {/* Menu Items */}
      <div className="menus flex-grow-1 ms-3">
        <ul className="d-flex gap-4 list-unstyled mb-0">
          <li>
            <Link to="/" onClick={() => handleMenuClick(0)} className="text-decoration-none">
              <p className={selectedMenu === 0 ? activeMenuClass : menuClass}>Dashboard</p>
            </Link>
          </li>
          <li>
            <Link to="/Orders" onClick={() => handleMenuClick(1)} className="text-decoration-none">
              <p className={selectedMenu === 1 ? activeMenuClass : menuClass}>Orders</p>
            </Link>
          </li>
          <li>
            <Link to="/Holdings" onClick={() => handleMenuClick(2)} className="text-decoration-none">
              <p className={selectedMenu === 2 ? activeMenuClass : menuClass}>Holdings</p>
            </Link>
          </li>
          <li>
            <Link to="/Positions" onClick={() => handleMenuClick(3)} className="text-decoration-none">
              <p className={selectedMenu === 3 ? activeMenuClass : menuClass}>Positions</p>
            </Link>
          </li>
          <li>
            <Link to="/Funds" onClick={() => handleMenuClick(4)} className="text-decoration-none">
              <p className={selectedMenu === 4 ? activeMenuClass : menuClass}>Funds</p>
            </Link>
          </li>
          <li>
            <Link to="/Apps" onClick={() => handleMenuClick(6)} className="text-decoration-none">
              <p className={selectedMenu === 6 ? activeMenuClass : menuClass}>Apps</p>
            </Link>
          </li>
        </ul>
      </div>

      {/* Profile */}
      <div className="profile ms-auto position-relative" onClick={handleProfileClick}>
        <div className="avatar bg-primary text-white rounded-circle d-flex align-items-center justify-content-center" style={{ width: "40px", height: "40px", cursor: "pointer" }}>
          {user ? user.name.slice(0, 2).toUpperCase() : "??"}
        </div>
        <p className="username ms-2 mb-0">{user ? user.name : "USER"}</p>

        {/* Dropdown */}
        {isProfileDropdownOpen && (
          <div
            className="profile-dropdown shadow"
            style={{
              position: "absolute",
              top: "50px",
              right: "0",
              background: "#fff",
              padding: "10px",
              borderRadius: "8px",
              minWidth: "120px",
              zIndex: 1000,
            }}
          >
            <button onClick={handleLogout} className="btn btn-danger w-100">
              Logout
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Menu;
