import React, { useState, useContext } from 'react';
import { NavLink } from 'react-router-dom'; // ✅ Use NavLink instead of Link
import axios from 'axios';
import GeneralContext from './GeneralContext'; // Make sure this path is correct

const Menu = () => {
  const [isProfileDropdownOpen, setIsProfileDropdownOpen] = useState(false);

  // ✅ Get the user directly from the central context
  const { user } = useContext(GeneralContext);

  const handleProfileClick = () => {
    setIsProfileDropdownOpen((prev) => !prev);
  };

  const handleLogout = async () => {
    try {
      await axios.post("http://localhost:3002/api/logout", {}, { withCredentials: true });
      window.location.href = "http://localhost:3000";
    } catch (err) {
      console.error("Logout failed", err);
    }
  };

  // ✅ This function is now used by NavLink to apply the correct class for styling
  const getNavLinkClass = ({ isActive }) => {
    const menuClass = "menu";
    const activeMenuClass = "menu selected";
    return isActive ? activeMenuClass : menuClass;
  };

  return (
    <div className="menu-container">
      <NavLink to="/">
        <img src="/logo.png" style={{ width: "50px" }} alt="Logo" />
      </NavLink>
      <div className="menus">
        <ul>
          {/* ✅ Use NavLink and the getNavLinkClass function for styling */}
          <li><NavLink to="/dashboard" className={getNavLinkClass}>Dashboard</NavLink></li>
          <li><NavLink to="/orders" className={getNavLinkClass}>Orders</NavLink></li>
          <li><NavLink to="/holdings" className={getNavLinkClass}>Holdings</NavLink></li>
          <li><NavLink to="/positions" className={getNavLinkClass}>Positions</NavLink></li>
          <li><NavLink to="/funds" className={getNavLinkClass}>Funds</NavLink></li>
          <li><NavLink to="/apps" className={getNavLinkClass}>Apps</NavLink></li>
        </ul>
        <hr />
        <div className="profile" onClick={handleProfileClick}>
          <div className="avatar">
            {user ? user.name.slice(0, 2).toUpperCase() : "??"}
          </div>
          <p className="username">{user ? user.name : "USER"}</p>
        </div>
        {isProfileDropdownOpen && (
          <div className="profile-dropdown">
            <button onClick={handleLogout} className="btn btn-danger w-100">
              Logout
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Menu;
