import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import GeneralContext from './GeneralContext'; // Make sure this path is correct

const Orders = () => {
  // ✅ Get the orders array AND the functions to open trade windows
  const { orders, openBuyWindow, openSellWindow } = useContext(GeneralContext);

  if (!orders) {
    return <p>Loading orders...</p>;
  }

  return (
    <>
      <h3 className="title">Orders ({orders.length})</h3>
      {orders.length === 0 ? (
        <div className="no-orders">
          <p>You haven't placed any orders today</p>
          <Link to={"/"} className="btn">Get started</Link>
        </div>
      ) : (
        <div className="order-table">
          <table>
            <thead>
              <tr>
                <th>Instrument</th>
                <th>Qty.</th>
                <th>Price</th>
                <th>Mode</th>
                <th>Action</th> {/* ✅ Add a new column header */}
              </tr>
            </thead>
            <tbody>
              {orders.map((order, index) => {
                const modeClass = order.mode?.toUpperCase() === "BUY" ? "profit" : "loss";
                return (
                  <tr key={index}>
                    <td>{order.name}</td>
                    <td>{order.qty}</td>
                    <td>{Number(order.price).toFixed(2)}</td>
                    <td className={modeClass}>{order.mode}</td>

                    {/* ✅ Add a new table cell with the conditional button */}
                    <td>
                      {order.mode === 'BUY' && (
                        <button 
                          className="sell-action-btn" 
                          style={{}}// Add your own CSS class for styling
                          onClick={() => openSellWindow(order.name)}
                        >
                          Sell
                        </button>
                      )}
                      {order.mode === 'SELL' && (
                        <button 
                          className="buy-action-btn" // Add your own CSS class for styling
                          onClick={() => openBuyWindow(order.name)}
                        >
                          Buy
                        </button>
                      )}
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      )}
    </>
  );
};

export default Orders;
