import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";

const Menu = () => {
  const [selectedMenu, setSelectedMenu] = useState(0);
  const [isProfileDropdownOpen, setIsProfileDropdownOpen] = useState(false);
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const res = await axios.get("http://localhost:3002/me", {
          withCredentials: true,
        });
        // Correctly set the user from the response data
        setUser(res.data);
      } catch (err) {
        console.error("Not authenticated:", err);
        // If the /me route fails, redirect to the login page
        navigate("/login");
      }
    };
    fetchUser();
  }, [navigate]);

  const handleMenuClick = (index) => {
    setSelectedMenu(index);
  };

  const handleProfileClick = () => {
    setIsProfileDropdownOpen((prev) => !prev);
  };

  const handleLogout = async () => {
    try {
      // Correctly call the /api/logout endpoint
      await axios.post("http://localhost:3002/api/logout", {}, { withCredentials: true });
      setUser(null);
      // Redirect to the public homepage
      window.location.href = "http://localhost:3000";
    } catch (err) {
      console.error("Logout failed", err);
    }
  };

  const menuClass = "menu";
  const activeMenuClass = "menu selected";

  return (
    <div className="menu-container">
      {/* Logo */}
      <Link to="/">
        <img src="/logo.png" style={{ width: "50px" }} alt="Logo" />
      </Link>

      {/* Menu Items */}
      <div className="menus">
        <ul>
          {/* Your menu items... */}
        </ul>
        <hr />

        {/* Profile */}
        <div className="profile" onClick={handleProfileClick}>
          <div className="avatar" style={{ /* your styles */ }}>
            {user ? user.name.slice(0, 2).toUpperCase() : "..."}
          </div>
          <p className="username">{user ? user.name : "Loading..."}</p>
        </div>

        {/* Dropdown */}
        {isProfileDropdownOpen && (
          <div className="profile-dropdown" style={{ /* your styles */ }}>
            <button onClick={handleLogout} className="btn btn-danger w-100">
              Logout
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Menu;
