import React, { useContext, useMemo } from 'react';
import GeneralContext from '../GeneralContext'; // Make sure the path is correct

// A helper function to format numbers into the k/L/Cr style
const formatIndianCurrency = (num) => {
  if (num >= 10000000) {
    return (num / 10000000).toFixed(2) + 'Cr';
  }
  if (num >= 100000) {
    return (num / 100000).toFixed(2) + 'L';
  }
  if (num >= 1000) {
    return (num / 1000).toFixed(2) + 'k';
  }
  return num.toFixed(2);
};

const Summary = () => {
  // 1. Get user and holdings data from the central context
  const { user, holdings } = useContext(GeneralContext);

  // 2. Calculate summary data from the holdings array
  // useMemo ensures these calculations only run when the holdings data changes
  const holdingsSummary = useMemo(() => {
    if (!holdings || holdings.length === 0) {
      return {
        investment: 0,
        currentValue: 0,
        totalPandL: 0,
        pAndLPercentage: 0,
      };
    }

    const totalInvestment = holdings.reduce((acc, holding) => acc + holding.avg * holding.qty, 0);
    const currentValue = holdings.reduce((acc, holding) => acc + holding.price * holding.qty, 0);
    const totalPandL = currentValue - totalInvestment;
    const pAndLPercentage = totalInvestment === 0 ? 0 : (totalPandL / totalInvestment) * 100;

    return {
      investment: totalInvestment,
      currentValue: currentValue,
      totalPandL: totalPandL,
      pAndLPercentage: pAndLPercentage,
    };
  }, [holdings]);

  // Show a loading state until the data is ready
  if (!user || !holdings) {
    return <div>Loading summary...</div>;
  }

  const isProfit = holdingsSummary.totalPandL >= 0;

  return (
    <>
      <div className="username">
        {/* Display the real user's name */}
        <h6>Hi, {user.name}!</h6>
        <hr className="divider" />
      </div>

      <div className="section">
        <span>
          <p>Equity</p>
        </span>
        <div className="data">
          {/* Note: Margin data would need to come from another API call */}
          <div className="first">
            <h3>3.74k</h3>
            <p>Margin available</p>
          </div>
          <hr />
          <div className="second">
            <p>Margins used <span>0</span></p>
            <p>Opening balance <span>3.74k</span></p>
          </div>
        </div>
        <hr className="divider" />
      </div>

      <div className="section">
        <span>
          {/* Display the real number of holdings */}
          <p>Holdings ({holdings.length})</p>
        </span>
        <div className="data">
          <div className="first">
            {/* Display the calculated P&L */}
            <h3 className={isProfit ? 'profit' : 'loss'}>
              {formatIndianCurrency(holdingsSummary.totalPandL)}
              <small>{isProfit ? '+' : ''}{holdingsSummary.pAndLPercentage.toFixed(2)}%</small>
            </h3>
            <p>P&L</p>
          </div>
          <hr />
          <div className="second">
            {/* Display the calculated current and investment values */}
            <p>Current Value <span>{formatIndianCurrency(holdingsSummary.currentValue)}</span></p>
            <p>Investment <span>{formatIndianCurrency(holdingsSummary.investment)}</span></p>
          </div>
        </div>
        <hr className="divider" />
      </div>
    </>
  );
};

export default Summary;
